CREATE TABLE anunciante (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, direccion VARCHAR(100) NOT NULL, localidad VARCHAR(50), telefono VARCHAR(50), email VARCHAR(50), web VARCHAR(150), facebook VARCHAR(150), anuncio TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE anunciante_foto (id BIGINT AUTO_INCREMENT, anunciante_id BIGINT, filename VARCHAR(255), caption VARCHAR(255) NOT NULL, INDEX anunciante_id_idx (anunciante_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE categoria (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE novedad (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, texto LONGTEXT, tipo VARCHAR(255), INDEX novedad_tipo_idx (tipo), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE novedad (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, texto LONGTEXT, tipo VARCHAR(255), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE sub_categoria (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, categoria_id BIGINT NOT NULL, INDEX categoria_id_idx (categoria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE sub_categoria_anunciante (id BIGINT AUTO_INCREMENT, sub_categoria_id BIGINT NOT NULL, anunciante_id BIGINT NOT NULL, INDEX anunciante_id_idx (anunciante_id), INDEX sub_categoria_id_idx (sub_categoria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE anunciante_foto ADD CONSTRAINT anunciante_foto_anunciante_id_anunciante_id FOREIGN KEY (anunciante_id) REFERENCES anunciante(id) ON DELETE CASCADE;
ALTER TABLE sub_categoria ADD CONSTRAINT sub_categoria_categoria_id_categoria_id FOREIGN KEY (categoria_id) REFERENCES categoria(id) ON DELETE CASCADE;
ALTER TABLE sub_categoria_anunciante ADD CONSTRAINT sub_categoria_anunciante_sub_categoria_id_sub_categoria_id FOREIGN KEY (sub_categoria_id) REFERENCES sub_categoria(id);
ALTER TABLE sub_categoria_anunciante ADD CONSTRAINT sub_categoria_anunciante_anunciante_id_anunciante_id FOREIGN KEY (anunciante_id) REFERENCES anunciante(id);
