CREATE TABLE anunciante (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, direccion VARCHAR(100) NOT NULL, localidad VARCHAR(50), telefono VARCHAR(50), email VARCHAR(50), web VARCHAR(150), facebook VARCHAR(150), anuncio TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE anunciante_foto (id BIGINT AUTO_INCREMENT, anunciante_id BIGINT, filename VARCHAR(255), caption VARCHAR(255) NOT NULL, INDEX anunciante_id_idx (anunciante_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE categoria (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, codigo VARCHAR(150) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE galeria (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE galeria_foto (id BIGINT AUTO_INCREMENT, galeria_id BIGINT, filename VARCHAR(255), INDEX galeria_id_idx (galeria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE novedad (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, resumen LONGTEXT, texto LONGTEXT, filename VARCHAR(100), tipo VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE novedad (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, resumen LONGTEXT, texto LONGTEXT, filename VARCHAR(100), tipo VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX novedad_tipo_idx (tipo), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE publicidad (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, codigo BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE publicidad_foto (id BIGINT AUTO_INCREMENT, publicidad_id BIGINT, filename VARCHAR(255), nombre_original VARCHAR(255), INDEX publicidad_id_idx (publicidad_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE revista (id BIGINT AUTO_INCREMENT, titulo VARCHAR(100) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE revista_foto (id BIGINT AUTO_INCREMENT, revista_id BIGINT, filename VARCHAR(255), nombre_original VARCHAR(255), orden BIGINT, INDEX revista_id_idx (revista_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE sub_categoria (id BIGINT AUTO_INCREMENT, nombre VARCHAR(150) NOT NULL, categoria_id BIGINT NOT NULL, INDEX categoria_id_idx (categoria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE sub_categoria_anunciante (id BIGINT AUTO_INCREMENT, sub_categoria_id BIGINT NOT NULL, anunciante_id BIGINT NOT NULL, INDEX anunciante_id_idx (anunciante_id), INDEX sub_categoria_id_idx (sub_categoria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
ALTER TABLE anunciante_foto ADD CONSTRAINT anunciante_foto_anunciante_id_anunciante_id FOREIGN KEY (anunciante_id) REFERENCES anunciante(id) ON DELETE CASCADE;
ALTER TABLE galeria_foto ADD CONSTRAINT galeria_foto_galeria_id_galeria_id FOREIGN KEY (galeria_id) REFERENCES galeria(id) ON DELETE CASCADE;
ALTER TABLE publicidad_foto ADD CONSTRAINT publicidad_foto_publicidad_id_publicidad_id FOREIGN KEY (publicidad_id) REFERENCES publicidad(id) ON DELETE CASCADE;
ALTER TABLE revista_foto ADD CONSTRAINT revista_foto_revista_id_revista_id FOREIGN KEY (revista_id) REFERENCES revista(id) ON DELETE CASCADE;
ALTER TABLE sub_categoria ADD CONSTRAINT sub_categoria_categoria_id_categoria_id FOREIGN KEY (categoria_id) REFERENCES categoria(id) ON DELETE CASCADE;
ALTER TABLE sub_categoria_anunciante ADD CONSTRAINT sub_categoria_anunciante_sub_categoria_id_sub_categoria_id FOREIGN KEY (sub_categoria_id) REFERENCES sub_categoria(id);
ALTER TABLE sub_categoria_anunciante ADD CONSTRAINT sub_categoria_anunciante_anunciante_id_anunciante_id FOREIGN KEY (anunciante_id) REFERENCES anunciante(id);
